/**
 * Badge Atom
 * Status indicators, counts, labels
 */

import styles from './Badge.module.css';
import { cn } from '../../../utils/cn';

export function Badge({
  variant = 'solid',
  color = 'red',
  size = 'md',
  dot = true,
  pulse = false,
  children,
  className,
  ...props
}) {
  const showDot = variant === 'solid' ? true : dot;

  return (
    <span
      className={cn(
        styles.badge,
        styles[variant],
        styles[color],
        styles[size],
        className
      )}
      {...props}
    >
      {showDot && (
        <span
          className={cn(styles.dot, pulse && styles.pulse)}
          aria-hidden="true"
        />
      )}
      {children}
    </span>
  );
}

export default Badge;
