/**
 * Icon Atom
 * SVG icon component with size variants
 * Requires icon paths to be provided via iconPaths prop or global registry
 */

import styles from './Icon.module.css';
import { cn } from '../../../utils/cn';

const SIZES = {
  xs: 12,
  sm: 16,
  md: 20,
  lg: 24,
  xl: 32,
  '2xl': 48,
};

// Default icon paths - projects should override with their own
const DEFAULT_ICON_PATHS = {
  'chevron-right': 'M9 5l7 7-7 7',
  'chevron-left': 'M15 19l-7-7 7-7',
  'chevron-down': 'M19 9l-7 7-7-7',
  'chevron-up': 'M5 15l7-7 7 7',
  close: 'M6 18L18 6M6 6l12 12',
  check: 'M5 13l4 4L19 7',
  plus: 'M12 5v14m-7-7h14',
  minus: 'M5 12h14',
  search: 'M21 21l-6-6m2-5a7 7 0 11-14 0 7 7 0 0114 0z',
  menu: 'M4 6h16M4 12h16M4 18h16',
  user: 'M20 21v-2a4 4 0 00-4-4H8a4 4 0 00-4 4v2m8-10a4 4 0 100-8 4 4 0 000 8z',
  settings: 'M10.325 4.317c.426-1.756 2.924-1.756 3.35 0a1.724 1.724 0 002.573 1.066c1.543-.94 3.31.826 2.37 2.37a1.724 1.724 0 001.065 2.572c1.756.426 1.756 2.924 0 3.35a1.724 1.724 0 00-1.066 2.573c.94 1.543-.826 3.31-2.37 2.37a1.724 1.724 0 00-2.572 1.065c-.426 1.756-2.924 1.756-3.35 0a1.724 1.724 0 00-2.573-1.066c-1.543.94-3.31-.826-2.37-2.37a1.724 1.724 0 00-1.065-2.572c-1.756-.426-1.756-2.924 0-3.35a1.724 1.724 0 001.066-2.573c-.94-1.543.826-3.31 2.37-2.37.996.608 2.296.07 2.572-1.065z M15 12a3 3 0 11-6 0 3 3 0 016 0z',
  home: 'M3 12l2-2m0 0l7-7 7 7M5 10v10a1 1 0 001 1h3m10-11l2 2m-2-2v10a1 1 0 01-1 1h-3m-6 0a1 1 0 001-1v-4a1 1 0 011-1h2a1 1 0 011 1v4a1 1 0 001 1m-6 0h6',
};

// List of icons that use stroke instead of fill
const STROKE_ICONS = new Set([
  'chevron-right', 'chevron-left', 'chevron-down', 'chevron-up',
  'close', 'check', 'plus', 'minus', 'search', 'menu', 'user', 'settings', 'home'
]);

export function Icon({
  name,
  size = 'md',
  color,
  className,
  style,
  iconPaths = DEFAULT_ICON_PATHS,
  'aria-label': ariaLabel,
  ...props
}) {
  const pixelSize = typeof size === 'number' ? size : SIZES[size] || SIZES.md;
  const path = iconPaths[name];

  if (!path) {
    if (import.meta.env.DEV) console.warn(`[ICON] Icon "${name}" not found`);
    return null;
  }

  const isStroke = STROKE_ICONS.has(name);
  const isDecorative = !ariaLabel;

  return (
    <svg
      width={pixelSize}
      height={pixelSize}
      viewBox="0 0 24 24"
      fill="none"
      xmlns="http://www.w3.org/2000/svg"
      className={cn(styles.icon, styles[size], className)}
      style={style}
      aria-hidden={isDecorative}
      aria-label={ariaLabel}
      role={isDecorative ? undefined : 'img'}
      data-icon={name}
      {...props}
    >
      <path
        d={path}
        fill={isStroke ? 'none' : (color || 'currentColor')}
        stroke={isStroke ? (color || 'currentColor') : 'none'}
        strokeWidth={isStroke ? 1.5 : 0}
        strokeLinecap="round"
        strokeLinejoin="round"
      />
    </svg>
  );
}

export default Icon;
