/**
 * DashboardGrid Molecule
 * Responsive grid layout for department/dashboard cards
 * Auto-fit columns: 4 desktop, 2 tablet, 1 mobile
 */

import styles from './DashboardGrid.module.css';
import { cn } from '../../../utils/cn';

// Map prop values to camelCase class names
const COLUMN_CLASSES = {
  'auto': 'columnsAuto',
  '2': 'columns2',
  '3': 'columns3',
  '4': 'columns4',
};

const GAP_CLASSES = {
  'sm': 'gapSm',
  'md': 'gapMd',
  'lg': 'gapLg',
};

export function DashboardGrid({
  children,
  columns = 'auto',
  gap = 'md',
  className,
}) {
  const gridClasses = cn(
    styles.grid,
    styles[COLUMN_CLASSES[columns]],
    styles[GAP_CLASSES[gap]],
    className
  );

  return <div className={gridClasses}>{children}</div>;
}

export default DashboardGrid;
