/**
 * DashboardRow Molecule
 * FranchiseOS-compliant row-based layout for asymmetric card arrangements
 * Follows DESIGN-RULES.md Section 10: Grid Layout Types - Row-Based
 */

import styles from './DashboardRow.module.css';
import { cn } from '../../../utils/cn';

export function DashboardRow({
  layout = 'equal',
  gap = 'md',
  children,
  className,
}) {
  /**
   * Layout options (based on FranchiseOS Card Size Classes):
   * - 'equal': All children equal width
   * - 'large-small': 2/3 + 1/3 split
   * - 'small-large': 1/3 + 2/3 split
   * - 'half': 1/2 + 1/2 split
   * - 'thirds': 1/3 + 1/3 + 1/3 split
   * - 'wide-narrow-narrow': 2/3 + 1/6 + 1/6 split
   */

  const rowClasses = cn(
    styles.row,
    styles[`layout${layout.split('-').map(w => w.charAt(0).toUpperCase() + w.slice(1)).join('')}`],
    styles[`gap${gap.charAt(0).toUpperCase()}${gap.slice(1)}`],
    className
  );

  return <div className={rowClasses}>{children}</div>;
}

export default DashboardRow;
