/**
 * Auth Service - API calls for authentication
 */

const API_BASE = import.meta.env.VITE_API_URL || 'https://api.stafull.com';
const TOKEN_KEY = 'stafull_token';
const REFRESH_KEY = 'stafull_refresh';

/**
 * Get stored tokens
 */
export function getStoredTokens() {
  return {
    accessToken: localStorage.getItem(TOKEN_KEY),
    refreshToken: localStorage.getItem(REFRESH_KEY),
  };
}

/**
 * Store tokens
 */
export function storeTokens(accessToken, refreshToken) {
  if (accessToken) localStorage.setItem(TOKEN_KEY, accessToken);
  if (refreshToken) localStorage.setItem(REFRESH_KEY, refreshToken);
}

/**
 * Clear tokens
 */
export function clearTokens() {
  localStorage.removeItem(TOKEN_KEY);
  localStorage.removeItem(REFRESH_KEY);
}

/**
 * Make authenticated API request
 */
async function authFetch(endpoint, options = {}) {
  const { accessToken } = getStoredTokens();

  const headers = {
    'Content-Type': 'application/json',
    ...options.headers,
  };

  if (accessToken) {
    headers.Authorization = `Bearer ${accessToken}`;
  }

  const response = await fetch(`${API_BASE}${endpoint}`, {
    ...options,
    headers,
  });

  // Handle token expiry
  if (response.status === 401) {
    const data = await response.json();
    if (data.code === 'INVALID_TOKEN' || data.code === 'SESSION_TIMEOUT') {
      // Try to refresh token
      const refreshed = await refreshAccessToken();
      if (refreshed) {
        // Retry with new token
        const newTokens = getStoredTokens();
        headers.Authorization = `Bearer ${newTokens.accessToken}`;
        return fetch(`${API_BASE}${endpoint}`, { ...options, headers });
      }
    }
  }

  return response;
}

/**
 * Login with email and password
 */
export async function login(email, password) {
  const response = await fetch(`${API_BASE}/api/auth/login`, {
    method: 'POST',
    headers: { 'Content-Type': 'application/json' },
    body: JSON.stringify({ email, password }),
  });

  const data = await response.json();

  if (response.ok && data.tokens) {
    storeTokens(data.tokens.accessToken, data.tokens.refreshToken);
  }

  return { ok: response.ok, data };
}

/**
 * Register new user
 */
export async function register(userData) {
  const response = await fetch(`${API_BASE}/api/auth/register`, {
    method: 'POST',
    headers: { 'Content-Type': 'application/json' },
    body: JSON.stringify(userData),
  });

  return { ok: response.ok, data: await response.json() };
}

